document.addEventListener('DOMContentLoaded',()=>{const counters=document.querySelectorAll('.counter');const speed=200;counters.forEach(counter=>{const updateCount=()=>{const target=+counter.getAttribute('data-target');const count=+counter.innerText;const inc=target / speed;if(count<target){counter.innerText=Math.ceil(count+inc);setTimeout(updateCount,1);}else{counter.innerText=target;}};updateCount();});const accordionHeaders=document.querySelectorAll('.accordion-header');accordionHeaders.forEach(header=>{header.addEventListener('click',()=>{const accordionContent=header.nextElementSibling;header.classList.toggle('active');accordionContent.classList.toggle('active');if(accordionContent.classList.contains('active')){accordionContent.style.maxHeight=accordionContent.scrollHeight+'px';}else{accordionContent.style.maxHeight=0;}});});});